export async function loader({ params }) {
    const productId = params.id;
    let cachedData = null;

    // Check if data is already cached
    const cachedItem = localStorage.getItem(`product_${productId}`);
    if (cachedItem) {
        cachedData = JSON.parse(cachedItem);
        return defer({ Data: Promise.resolve(cachedData) }); // Return resolved promise from cache
    }

    // Fetch data from Firestore if not cached
    try {
        const DataPromise = GetProductById(productId);

        // Store data in cache once fetched
        DataPromise.then(data => {
            localStorage.setItem(`product_${productId}`, JSON.stringify(data));
        });

        return defer({ Data: DataPromise });
    } catch (error) {
        console.error('Error fetching product from Firestore:', error);
        throw new Error('Failed to fetch product');
    }
}



